import React from "react"
import { tableAPI } from "../api/tables/tables-api"
import { BaseThunkType, InferActionsType } from "./redux-store"

const SET_NEW_TABLE = 'SET_NEW_TABLE'
const UPDATE_TABLE = 'UPDATE_TABLE'

type ActionsType = InferActionsType<typeof actions>
type InitialStateType = typeof initialState
export type ThunkType = BaseThunkType<ActionsType>

export type tableFullType = {
    tableData:tableType
    title:string
    __v:number
    _id:string
}
export type tableType = {
    items:Array<string>
    days:Array<string | number | undefined>
    data:Array<dataType>
}
export type dataType = {
    name:string
    status: Array<string>
}

const initialState = {
    tables:[] as Array<tableFullType>
}

const tablesReducer = (state:InitialStateType = initialState, action:ActionsType):InitialStateType => {
    switch(action.type) {
        case SET_NEW_TABLE : {
            return {
                ...state,
                tables:[...state.tables,action.table]
            }
        } 
        case UPDATE_TABLE: {
            const updatedTables:Array<tableFullType> = state.tables.map((table) => {
                if (table._id === action.table._id) {
                    return action.table;
                }
                return table;
            });
            return {
                ...state,
                tables: updatedTables,
            };
        }
        default : {
            return state
        }
    }
}
export const actions = {
    setTables:(tables:Array<tableFullType>) => ({type:SET_TABLE,tables}),
    setNewTable:(table:tableFullType) => ({type:SET_NEW_TABLE,table}),
    updateTable: (table:tableFullType) => ({type: UPDATE_TABLE,table})
}


export const getTables = ():ThunkType => async (dispatch) => {
    let response = await tableAPI.getTables()
    if(response.status === 200) {
        dispatch(actions.setNewTable(response.data))
    }
}
export const createNewTable = ({items,days,data}):ThunkType => async (dispatch) => {
    let response = await tableAPI.createTable({items,days,data});
    if(response.status === 200) {
        dispatch(actions.setNewTable(response.data))
    }
}

export default tablesReducer
