import React from 'react'

class ProfileStatus extends React.Component {
    state = {
        editMod:false
    }
    activate
    render() {
        return(
            <>
                <div>
                    {!this.state.editMod?<span onDoubleClick={ () => {this.state.editMod = true} }>{this.props.status}</span>:<input value={this.props.status}></input>}
                </div>
            </>
        )
    }
}
export default ProfileStatus;
