import React, { useState }  from 'react'
import {useDispatch,useSelector} from 'react-redux'
import styles from '../FormCreateTable.module.css'
import { selectItems } from '../../../../redux/createTable-selectors.ts'

const DaysInput = (props) => {
    const [valueOfDays,setValueOfDays] = useState('')
    return (
        <fieldset className={styles.fildset}>
            <legend className={styles.legend}>Days</legend> 
            <input type="number" min="1" max="30" placeholder='Days' value={valueOfDays} onChange={(e) => {props.setValueOfDays(e.currentTarget.value)}} className={styles.inputs}/>
        </fieldset>
    )
}

export default DaysInput
