import {createSelector} from 'reselector'

export const getUsersAll = (state) => {
    return state.usersPage.users
}

export const getUsersSelector = (state) => {
    return getUsersAll(state).filter(u => true)
}

export const getUsersSuperSelector = createSelector(() => {
    return getUsersAll(state)
    }
)
    

export const getPageSize = (state) => {
    return state.usersPage.pageSize
}
export const getTotalUsersCount = (state) => {
    return state.usersPage.totalUsersCount
}
export const getCurrentPage = (state) => {
    return state.usersPage.currentPage
}
export const getIsFetching = (state) => {
    return state.usersPage.isFetching
}
export const getFolowingInProgress = (state) => {
    return state.usersPage.folowingInProgress
}
