import React from 'react';
import Dialogs from './Dialogs';
import {addMessageActionCreater,updateNewMessageTextActionCreater} from './../../redux/dialogs-reducer'
import StoreContext from './../../StoreContext';
import connect from './react-redux'

const DialogsContainer = (props) =>{
    
        return <StoreContext.Consumer>
        { store => {
            let onAddMessage = () => {
                store.dispatch(addMessageActionCreater())
            }
        
            let onMessageChange = (message) => {
                let action = updateNewMessageTextActionCreater(message);
                store.dispatch(action);
            }
            return (
                <Dialogs 
                    addMessage={onAddMessage}
                    onMessageChange={onMessageChange}
                    dialogsPage={store.getState().dialogsPage}
                    h={props.h}
                />)
            }
        }
        </StoreContext.Consumer>
}

const SuperDialogsContainer = connect()()

export default DialogsContainer
