import { applyMiddleware, combineReducers ,legacy_createStore as createStore} from "redux";
import planReducer from "./plan-reducer.ts";
import tablesReducer from "./tables-reducer.ts";
import createTableReducer from "./createTable-reducer.ts";

type reducersType = typeof reducers
export type InferActionsType<T> = T extends {[key:string]: (...args:any[]) => infer U} ? U :never
export type AppStateType = ReturnType<reducersType>

const reducers = combineReducers({
    planPage:planReducer,
    tablesPage:tablesReducer,
    createTable:createTableReducer
})

// @ts-ignore
const composeEnhancers = window.__REDUX_DEVTOOLS_EXTENSION_COMPOSE__ || compose;
const store = createStore(reducers, composeEnhancers(applyMiddleware(thunkMiddleware)))
// @ts-ignore
window.store = store
export default store
