import so from './MyPosts.module.css';
import Post from './Post/Post';
import React from 'react';
import {reduxForm,Field} from 'redux-form'
import {maxLengthCreator, requered} from './../../../utials/validators/validators'


const MyPostsForm = (props) => {
    return (
        <form onSubmit={props.handleSubmit}>
            <Field component='textarea' name='post' placeholder='Пост' value={props.newPostText} validate={[requered,maxLengthCreator(10)]} />
            <br />
            <button>Add post</button>
        </form>
    )
}

const MyPostsReduxForm = reduxForm({
    form:'ProfileAddPostForm'
})(MyPostsForm)


const MyPosts = (props) => {
    let postsElements = props.posts.map( post => <Post message={post.message} key={post.id} likecount={post.likesCount}/>)
    const onSubmit = (formData) => {
        props.addPost(formData.post)
    }
    
    /*
     
    let newPostElement = React.createRef();
   
    let onAddPosts = () => { 
        props.addPost()
    }
    
    let onPostChange = () => {
        let text = newPostElement.current.value;
        props.updateNewPostText(text);
    }


*/
    return (
        <div className={so.postsBlock}>
            <div>
                My posts
            </div>
                <MyPostsReduxForm onSubmit={onSubmit}/>
            <div className={so.posts}>
                {postsElements}
            </div>
        </div>
    )
}
/*
<textarea onChange={onPostChange} ref={newPostElement} value={props.newPostText} />
<button onClick={onAddPosts}>Add post</button>*/
export default MyPosts
