import React from 'react';
import s from './Preloader.module.css';

let Users = (props) => {

    
    let pagesCount = Math.ceil(props.totalUsersCount / props.pageSize);
    let pages = [];
    for(let cnt = 1; cnt <= pagesCount; cnt++){
        pages.push(cnt);
        
    }
        return (
            <section>
                <h2>{props.h}</h2>
                <ol className={s.ol}>
                    { pages.map (p => {
                        return <li className = {props.currentPage === p && s.selectedPage} onClick={(e) => {props.onPageChanged(p)}}>{p}</li>
                    })}
                </ol>
                <ul>
                    {
                    props.users.map( u => <li className={s.li} key={u.id}>
                        <span>
                            <img className={s.img} src={u.photos.small != null?u.photos.small:'/userNull.jpg'} alt="ava"/>
    
                            {u.followed?
                            <button onClick={() => {props.unfollow(u.id)}}>Unfollow</button>
                            :
                            <button onClick={() => {props.follow(u.id)}}>Follow</button>}     
                        </span>
                        <span>
                            <b>{u.name}</b>
                            <p>{u.status}</p>
                            <span>{"u.location.city"}</span>
                            <span>{"u.location.country"}</span>
                        </span>
                    </li>)
                    }
                </ul>
            </section>
        )
    }

export default Users;
