import { NavLink } from 'react-router-dom';
import s from './Header.module.css';
import React from 'react'
import { Avatar, Col, Menu, Row } from 'antd';
import { UserOutlined } from '@ant-design/icons';
import {Layout} from 'antd';
import { useSelector } from 'react-redux';
import { selectIsAuth } from '../../redux/auth-selectors';


export type MapPropsType = {
    isAuth:boolean
    login:string | null
}
export type DispatchPropsType = {
    loginout:() => void
}
const {Header} = Layout;
const AppHeader:React.FC<MapPropsType & DispatchPropsType> = (props) => {
    const isAuth = useSelector(selectIsAuth)
    const login = useSelector(selectCurrentUserLogin)
    return ( 
        <Header className="header">
        <div className="logo" />
        <Row>
          <Col span="20">
            <Menu theme="dark" mode="horizontal">
              <Menu.Item><NavLink to='/users'>Find users</NavLink></Menu.Item>
            </Menu>
          </Col>
          <Col span="4"><Avatar style={{ backgroundColor: '#87d068' }} icon={<UserOutlined />} /></Col>
        </Row>
      </Header>
      /*
        <div>
            <h1 className={s.h1}>UsiChat</h1>
            <div className={s.loginBlock}>
                {props.isAuth 
                ? <div>{props.login} - <button onClick={props.loginout}>Log out</button></div>
                :<NavLink to='/login'>Login</NavLink>}   
            </div>
        </div>*/
    )
}
export default AppHeader;
