import React from 'react'

export const updateObjectInArray = (items,itemId,objPropsName,newObjProps) => {
 
    items.map( u => {
    if(u[objPropsName] === itemId) {
      return {...u, ...newObjProps}
    }
    console.log(items)
    return u 
    
  })
}
