import React from 'react';
import Paginator from '../common/paginator/Paginator.tsx'
import User from './User.tsx'
import {UserType} from './../../types/types.ts'
import UsersSearchForm from './UsersSearchForm.tsx';
import { SearchFilterType, getUsers,follow,unfollow } from '../../redux/users-reducer.ts';
import { useDispatch, useSelector } from 'react-redux';
import { getCurrentPage, getFilter, getFolowingInProgress, getPageSize, getTotalUsersCount, getUsersSuperSelector } from '../../redux/users-selectors.ts';

type Props = {
    h:string
    unfollow:(userId:number) => void
    follow:(userId:number) => void
}
let Users:React.FC<Props> = ({...props}) => {
    const totalUsersCount = useSelector(getTotalUsersCount)
    const currentPage = useSelector(getCurrentPage)
    const pageSize = useSelector(getPageSize)
    const filter = useSelector(getFilter)
    const users = useSelector(getUsersSuperSelector)
    const folowingInProgress = useSelector(getFolowingInProgress)

    const dispatch = useDispatch()
    const onPageChanged = (pageNumber:number) => {
        dispatch(getUsers(pageNumber,pageSize,filter));
    }
    const onFilterChanged = (filter:SearchFilterType) => {
        dispatch(getUsers(1,pageSize,filter));
    }
    const follow = (userId:number) => {
        dispatch(follow(userId))
    }
    const unfollow = (userId:number) => {
        follow(userId)
    }
    return (
        <section>
            <h2>{props.h}</h2>
            <UsersSearchForm onFilterChanged={onFilterChanged}/>
            <Paginator currentPage={currentPage} onPageChanged={onPageChanged} totalItemsCount={totalUsersCount} pageSize={pageSize} portionSize={3}/>
            <ul>
                {
                users.map( u => <User user={u} 
                        key={u.id} 
                        folowingInProgress={folowingInProgress} 
                        unfollow={props.unfollow} 
                        follow={props.follow}
                    />)
                }
            </ul>
        </section>
    )
}


export default Users;
