import {ChatMessageType, chatAPI} from '../components/api/chat-api.ts'
import {authAPI} from '../components/api/auth-api';
import {FormAction, stopSubmit} from 'redux-form'
import {securityAPI} from '../components/api/security-api';
import {ResultCodeEnum, ResultCodeWithCaptchaEnum} from '../components/api/api';
import { BaseThunkType, InferActionsType } from './redux-store';
import { Dispatch } from 'redux';

const MESSAGES_RECEVIED = 'SN/chat/MESSAGES_RECEVIED'

let initialState =  {
    messages:[] as ChatMessageType[]
}
type ActionsType = InferActionsType<typeof actions>
type ThunkType = BaseThunkType<ActionsType | FormAction>

type initialState = typeof initialState
export const chatReducer = (state:initialState = initialState,action:ActionsType):initialState =>{
  switch(action.type) {
    case MESSAGES_RECEVIED: 
        return { 
            ...state,
            messages:[...state.messages,...action.payload]
        }
    default: 
        return state;
  }
}
const actions = {
  setMessages:(messages:ChatMessageType[]) => ({
    type:MESSAGES_RECEVIED, payload:messages
  })
}
let _newMessageHanbler:((messages:ChatMessageType[]) => void) | null = null



const newMessageHanbler = (dispatch:Dispatch) => {
    if(_newMessageHanbler !== null) return _newMessageHanbler
    _newMessageHanbler = (messages:ChatMessageType[]) => {
        dispatch(actions.setMessages(messages))
    }
    return _newMessageHanbler
} 
export const startMessagesListening = ():ThunkType => async (dispatch) => {
   chatAPI.subscribe(newMessageHanbler(dispatch))
}
export const stopMessagesListening = ():ThunkType => async (dispatch) => {
    chatAPI.unsubscribe(newMessageHanbler(dispatch))
 }
export default chatReducer;
