import React, { useEffect }  from 'react'
import styles from './Table.module.css'
import { useDispatch, useSelector } from 'react-redux'
import { Navigate, useParams } from 'react-router-dom'
import { selectTables } from '../../../../redux/tables-selectors.ts'
import { tableType } from '../../../../redux/tables-reducer'

const Table = () => {
    const dispatch = useDispatch()
    const {tableId} = useParams()
    const tables = useSelector(selectTables)
    const tableData:tableType | undefined = tables.find(table => table.id === tableId)
    const arrayOfDays = Array.from({ length: parseInt(tableData?.tableData.days) }, (v, i) =>  i + 1);   
    return (
        <>
        {!tableData ? <Navigate to="/tables" /> :
        <div className={styles.table}>
            <h2>{tableData.name}</h2>
            <table>
                <thead>
                    <tr>
                        {arrayOfDays.map(day => <th>{day}</th>)}
                    </tr>
                </thead>
            </table>
        </div>}
        </>
        
    )
}

export default Table
