import so from './Profileinfo.module.css';
import Preloader from './../../common/preloader/Preloader'
import ProfileStatusWithHooks from './ProfileStatusWithHooks';
import userPhoto from './../../common/userNull.jpg';
const ProfileInfo = (props) => {
    if(!props.profile) {
        return <Preloader />
    }
    let onMainPhotoSelected = (e) => {
        if(e.target.files.length){
            props.savePhoto(e.target.files[0])
        }
    }
    return(
        <div>
            <img className={so.img} src="https://proprikol.ru/wp-content/uploads/2020/08/kartinki-krutyh-mashin-57.jpg" alt="main"/>
            <div className={so.descBlock}>
                <img src={props.profile.photos.large || userPhoto} alt="ava" className={so.ava} />
                {props.isOwner && <input type="file" onChange={onMainPhotoSelected}/>}
                <ProfileData props={props.profile}/>
                <ProfileStatusWithHooks status={props.status} updateStatus={props.updateStatus}/>
            </div>
        </div>
    )
    const ProfileData = (props) => {
    debugger
    return <div>
            <div>
                <b>Full name: </b>{props.profile.fullName}
            </div>
            <div>
                <b>Looking for a job: </b>{props.profile.lookingForAJob ? 'yes' : 'no'}
            </div>
            {props.profile.lookingForAJob &&
                <div>
                    <b>Skills: </b> {props.profile.lookingForAJobDescription}
                </div>
            }
            <div>
                <b>About me: </b> {props.profile.aboutMe || '---'}
            </div>
            <div>
                <b>Contacts: </b> {Object.keys(props.profile.contacts).map(key => {
                    return  <Contact contactTitle={key} contactValue={props.profile.contacts[key]}/>
                })}
            </div>
        </div>
}
const Contact = ({contactTitle,contactValue}) => {
    return <div className={so.contact}><b>{contactTitle}</b>:{contactValue}</div>
}
}

export default ProfileInfo;
