import React from 'react';
import {follow,unfollow,getUsers, SearchFilterType} from '../../redux/users-reducer.ts'
import {connect}  from 'react-redux'
import Users from './Users.tsx';
import Preloader from '../common/preloader/Preloader.tsx';
import {compose} from 'redux'
import {UserType} from './../../types/types.ts'
import {getUsersSuperSelector,getCurrentPage,getFolowingInProgress,getIsFetching,getTotalUsersCount,getPageSize} from '../../redux/users-selectors.ts'
import { AppStateType } from '../../redux/redux-store.js';


type MapStateToPropsType = {
    currentPage:number
    pageSize:number
    isFetching:boolean
    totalUsersCount:number
    folowingInProgress:Array<number>
    users:Array<UserType>
}
type MapDispatchToPropsType = {
    unfollow:(userId:number) => void
    follow:(userId:number) => void
    getUsers:(currentPage:number,pageSize:number,term:string) => void
}

type OwnPropsType= {
    h:string
}
type PropsType = MapDispatchToPropsType & MapStateToPropsType & OwnPropsType

class UsersContainer extends React.Component<PropsType> {
    componentDidMount() {
        let {currentPage,pageSize,} = this.props
        this.props.getUsers(currentPage,pageSize,"");
    }
    onPageChanged = (pageNumber) => {
        let {pageSize,filter} = this.props
        this.props.getUsers(pageNumber,pageSize,filter.term);
    }
    onFilterChanged = (filter:SearchFilterType) => {
        let {currentPage,pageSize} = this.props
        this.props.getUsers(currentPage,pageSize,filter.term);
    }
    render() {

        return <>
        {this.props.isFetching?<Preloader/>:null}
            <Users
                totalUsersCount={this.props.totalUsersCount}
                pageSize={this.props.pageSize}
                currentPage={this.props.currentPage}
                onPageChanged={this.onPageChanged}
                users={this.props.users}
                follow={this.props.follow}
                unfollow={this.props.unfollow}
                onFilterChanged={this.onFilterChanged}
                h={this.props.h}
                filter={this.props.filter}
                folowingInProgress={this.props.folowingInProgress}
            />
        </>
    }
}
/*
let mapStateToProps = (state) => {
    return {
        users : state.usersPage.users,
        pageSize: state.usersPage.pageSize,
        totalUsersCount: state.usersPage.totalUsersCount,
        currentPage:state.usersPage.currentPage,
        isFetching:state.usersPage.isFetching,
        folowingInProgress:state.usersPage.folowingInProgress
    }
}
*/
let mapStateToProps = (state:AppStateType):MapStateToPropsType => {
    return {
        users : getUsersSuperSelector(state),
        pageSize: getPageSize(state),
        totalUsersCount: getTotalUsersCount(state),
        currentPage: getCurrentPage(state),
        isFetching: getIsFetching(state),
        folowingInProgress: getFolowingInProgress(state),
        filter:state.usersPage.filter
    }
}

export default compose(
    connect<MapStateToPropsType,MapDispatchToPropsType,OwnPropsType,AppStateType>(mapStateToProps,{follow,unfollow,getUsers}),
)(UsersContainer)
