import React from 'react'
import {createField,Input,Textarea} from './../../common/formsControls/formsControsl'
import {Contact} from './Profileinfo'
import {reduxForm} from 'redux-form'
const ProfileDataForm = ({profile,isOwner,goToEditMode}) => {
    return <form>
        {isOwner && <button onClick={goToEditMode}>Save</button>}
            <div>
                <b>Full name: </b>{createField("Full name","fullname",Input,[])}
            </div>
            <div>
                <b>Looking for a job: </b>{createField("","lookingForAJob",Input,[],{type:'checkbox'})}
            </div>
                <div>
                    <b>Skills: </b>{createField("Skills","lookingForAJobDescription",Textarea,[])}
                </div>
            <div>
                <b>About me: </b>{createField("Фищге ьу","fullname",Input,[])} {profile.aboutMe || '---'}
            </div>

        </form>
}
const ProfileDataFormRedux = reduxForm({form:'edit-profile'})(ProfileDataForm)
export default ProfileDataFormRedux
