import { Navigate } from "react-router-dom";
import React from 'react';
import {connect}  from 'react-redux';
import { AppStateType } from "../redux/redux-store";

let mapStateToPropsForRedirect = (state:AppStateType) => ({
    isAuth:state.auth.isAuth
});
type MapPropsType = {
    isAuth:boolean
}
type DispacthPropsType = {
    fake:() => void
}
export function withAuthRedirect<WCP extends JSX.IntrinsicAttributes>(WrappedComponent:React.ComponentType<WCP> ) {
    const RedirectComponent:React.FC<DispacthPropsType & MapPropsType> = (props) => {
        let {isAuth,fake,...resProps} = props
        if(!isAuth) return <Navigate to='/login' />
        return <WrappedComponent {...resProps as WCP} />
    }
    let ConnectedAuthRedirectComponent = connect<MapPropsType,DispacthPropsType,WCP,AppStateType>(mapStateToPropsForRedirect,{fake:() => {}})(RedirectComponent);
    return ConnectedAuthRedirectComponent;
}
