import React from 'react'
import {reduxForm,Field} from 'redux-form'
import {Input} from './../common/formsControls/formsControsl'
import {requered,maxLengthCreator} from './../../utials/validators/validators'
import {connect} from 'react-redux'
import {login} from './../../redux/auth-reducer'
import { Navigate } from 'react-router-dom'

const maxLength10 = maxLengthCreator(10)
const LoginForm = (props) => {
    return (
            <form onSubmit={props.handleSubmit}>
                <div>
                    <Field placeholder='Login' name='email' component={Input} validate={[requered]}/>
                </div>
                <div>
                    <Field placeholder='Password' name='password' component={Input} validate={[requered,maxLength10]} type="password"/>
                </div>
                <div>
                    <Field component={Input} name='rememberMe' type='checkbox'/> remember me
                </div>
                <div>   
                    <button>Login</button>
                </div>
            </form>
    )
}

const LoginReduxForm = reduxForm({
    form:'login'
})(LoginForm)

const Login = (props) => {

    const onSubmit = (formData) => {
        props.login(formData.email,formData.password,formData.rememberMe)
    }
    return ( <section>
            <h2>{props.h}</h2>
            <LoginReduxForm onSubmit={onSubmit}/>
        </section>
    )
}

export default connect(null,{login})(Login)
