import {profileAPI} from './../components/api/api';
const ADD_POST = 'ADD-POST'
const UPDATE_NEW_POST_TEXT = 'UPDATE-NEW-POST-TEXT'
const SET_USER_PROFILE  = 'SET_USER_PROFILE'
const SET_STATUS  = 'SET_STATUS'
const DELETE_POST = 'DELETE_POST'


let initialState = {
  posts: [
      {'id':'1', 'message':'Hi,how are you?', 'likesCount': 22},
      {'id':'2', 'message':'It\'s my first post', 'likesCount': 15}
    ],
  profile:null,
  status:"",
}

const profileReducer = (state = initialState,action) =>{
    switch(action.type){
   
    case ADD_POST: {
        let newPost = {
          'id': 5, 
          'message':action.post,
          'likesCount': 0
        };
        return {
            ...state,
            posts: [...state.posts, newPost]
        }
      }
    case UPDATE_NEW_POST_TEXT:{
        return {
          ...state,
          newPostText:action.newText
        }
    }
    case SET_USER_PROFILE:{
      return {
        ...state,
        profile:action.profile
      }
    }
    case SET_STATUS:{
      return {
        ...state,
        status:action.status
      }
    }
    case DELETE_POST:{
      return {
        ...state,
        posts:state.posts.filter(p => p.id != action.postI)
      }
    }
    default: {
        return state;
    }
  }
}

export const addPostActionCreater = (post) => ({type:ADD_POST,post});
export const setUserProfile  = (profile) => ({type:SET_USER_PROFILE,profile});
export const updateNewPostTextActionCreater = (text) =>({ type:UPDATE_NEW_POST_TEXT, newText:text });
export const setStatus = (status) => ({ type:SET_STATUS, status:status });
export const deletePost = (postId) => ({ type:DELETE_POST,postId});


export const getProfile = (profileId) => { 
  return (dispatch) => {
    profileAPI.getProfile(profileId).then(data => {
      dispatch(setUserProfile(data));
    });
  }
}
export const getStatus = (profileId) => { 
  return (dispatch) => {
    profileAPI.getStatus(profileId).then(response => {
      dispatch(setStatus(response.data));
    });
  }
}
export const updateStatus = (status) => { 
  return (dispatch) => {
    profileAPI.updateStatus(status).then(response => {
      if(response.data.resultCode === 0) {
        dispatch(setStatus(status));
      }
    });
  }
}

export default profileReducer;
