import React, { FormEvent, useState } from 'react'
import styles from './List.module.css'
import {v1} from 'uuid'
import { useDispatch } from 'react-redux';
import {actions} from './../../../redux/plan-reducer.ts'

const SearchInput = () => {
    const [value,setValue] = useState('')
    const dispatch = useDispatch()
    const addPurpose = (e:FormEvent<HTMLFormElement>) => {
        e.preventDefault()        
        let day = new Date().getDate()
        let month = new Date().getMonth()
        let year = new Date().getFullYear()
        dispatch(actions.setNewPurpose({id:v1(),text:value,data:`${day}.`}))
    }
    return(
        <>
            <form onSubmit={(e) => addPurpose(e)}>
                <input type="text" placeholder="Ceate new part of your list" value={value} onChange={(e) => setValue(e.currentTarget.value)} className={styles.searchInput}/>
                <input type="submit"/>
            </form>
        </>
    )
}

export default SearchInput
