const SET_USER_DATA = 'SET_USER_DATAA'


let initialState =  {
    userId:null,
    email:null,
    login:null,
}

export const authReducer = (state = initialState,action) =>{
  switch(action.type) {
    case SET_USER_DATA: 
        return {
            ...state,
            ...state.data
        }
    default: 
        return state;
  }
}
export const setUserData = (userId,email,login) => ({
  type:SET_USER_DATA, data:{userId,email,login}
})
export default authReducer;
