import React, { useEffect }  from 'react'
import styles from './Tables.module.css'
import { useDispatch, useSelector } from 'react-redux';
import { selectTables } from '../../../../redux/tables-selectors.ts';
import {NavLink, useNavigate} from 'react-router-dom'
import Table from '../Table/Table.tsx';
import { tableFullType } from 'redux/tables-reducer';

const Tables = () => {
    const tables = useSelector(selectTables)
    const navigate = useNavigate()
    const dispatch = useDispatch()
    const createNewTable = () => {
        navigate('create-table')
    }
    useEffect(() => {
        dispatch(getTables())
    },[])
    return (
        <div className={styles.tables}>
            <ul className={styles.ul}>
            {tables.map((table:tableFullType) => 
            <li className={styles.li} key={table._id}>
                <NavLink to={`${table._id}`}>
                    <div>
                        <b>{table.title}</b>
                    </div>
                </NavLink>
            </li>
            )}
            <li className={styles.li} onClick={() => createNewTable()}><button className={styles.button}>+</button></li>
            </ul>
        </div>
    )
}

export default Tables
