import so from './Profileinfo.module.css';
import Preloader from './../../common/preloader/Preloader'
import ProfileStatusWithHooks from './ProfileStatusWithHooks';
import userPhoto from './../../common/userNull.jpg';
const ProfileInfo = (props) => {
    if(!props.profile) {
        return <Preloader />
    }
    onMainPhotoSelected()
    return(
        <div>
            <img className={so.img} src="https://proprikol.ru/wp-content/uploads/2020/08/kartinki-krutyh-mashin-57.jpg" alt="main"/>
            <div className={so.descBlock}>
                <img src={props.profile.photos.large || userPhoto} alt="ava" className={so.ava} />
                {props.isOwner && <input type="file" onChange={onMainPhotoSelected}/>}
                <ProfileStatusWithHooks status={props.status} updateStatus={props.updateStatus}/>
            </div>
        </div>
    )
}
export default ProfileInfo;
