import React from 'react'
import {reduxForm} from 'redux-form'
import {Input} from '../common/formsControls/formsControsl'
import {requered,maxLengthCreator} from '../../utials/validators/validators'
import {connect} from 'react-redux'
import {login} from '../../redux/auth-reducer'
import { Navigate } from 'react-router-dom'
import s from './../common/formsControls/formsControls.module.css'
import {createField} from '../common/formsControls/formsControsl.ts'

const maxLength10 = maxLengthCreator(10)
const LoginForm = ({handleSubmit,error,captchaUrl}) => {
    return (
            <form onSubmit={handleSubmit}>
                {createField('Login','email',Input,[requered])}
                {createField('Password','password',Input,[requered,maxLength10],{type:'password'})}
                {createField(null,'rememberMe',Input,null,{type:'checkbox'},'remember me')}
                {captchaUrl && <img src={captchaUrl} alt="1"/>}
                {captchaUrl && createField('Symbols','captcha',Input,[requered])}
                {error &&
                <div className={s.formSummaryError}>
                    {error}
                </div>}
                <div>   
                    <button>Login</button>
                </div>
            </form>
    )
}

const LoginReduxForm = reduxForm({
    form:'login'
})(LoginForm)

const Login = (props) => {

    const onSubmit = (formData) => {
        props.login(formData.email,formData.password,formData.rememberMe,formData.captcha)
    }
    if(props.isAuth) {
        return <Navigate to={'/profile'}/>
    }
    return ( <section>
            <h2>{props.h}</h2>
            <LoginReduxForm onSubmit={onSubmit} captchaUrl={props.captchaUrl}/>
        </section>
    )
}

type MapStatePropsType = {
    captchaUrl:string
    isAuth:boolean
}
const mapStateToProps = (state:App):MapStatePropsType => ({
    isAuth:state.auth.isAuth,
    captchaUrl:state.auth.captchaUrl
})
export default connect(mapStateToProps,{login})(Login)
