import React, { useState ,useEffect} from 'react'
import {useDispatch, useSelector} from 'react-redux'
import styles from './List.module.css'
import SearchInput from './SearchInput.tsx'
import TodosList from './TodosList.tsx'
import { Context } from '../context.ts'
import { actions, getTodos, setTodo } from '../../../redux/plan-reducer.ts'
import { selectTodos } from '../../../redux/plan-selectors.ts'

/*
    const [todos,setTodos] = useState([] as Array<Todo>)
    useEffect(() => {
        const row = localStorage.getItem('todos') || []
        //@ts-ignore
        setTodos(JSON.parse(row))
    },[])
    useEffect(() => {
        localStorage.setItem('todos',JSON.stringify(todos))
    },[todos])
    */

const List = () => {
    const options = {
        weekday: "long",
        year: "numeric",
        month: "long",
        day: "numeric",
    };
    const [todoTitle,setTodoTitle] = useState('')
    const todos = useSelector(selectTodos)
    const dispatch = useDispatch()
    const addTodo = (event:any) => {
        if(event.key === 'Enter' ||  event.target.type == "submit") {
            const newTodo = {
                title:todoTitle,
                data:new Date().toLocaleDateString("en-KZ",options),
                completed:false
            }
            dispatch(setTodo(newTodo))
            setTodoTitle('')
        }
    }
    const removeTodo = (id:number) => {
        dispatch(actions.deleteTodo(id))
    }

    const toggleTodo = (id:number) => {
        dispatch(actions.setStatus(id))
    }

    useEffect(() => {
        dispatch(getTodos())
    },[])

    return(
        <Context.Provider value={{removeTodo,toggleTodo}}>
        <div className={styles.list}>
            <div className={styles.form}>
                <input type="text" 
                    placeholder="Create new part of your list" 
                    value={todoTitle} 
                    onChange={(event) => setTodoTitle(event.target.value)} 
                    className={styles.searchInput+" "+styles.inputs}
                    onKeyDown={event => addTodo(event)}
                    />
                <input 
                    type="submit" 
                    className={styles.submit+" "+styles.inputs} 
                    value="ADD" 
                    onClick={event => addTodo(event)}
                    />
            </div>
            <TodosList todos={todos}/>
        </div>
        </Context.Provider>
    )
}

export default List
