import React,{useEffect, useState} from 'react'

type PropsType = {
    status:string
    
}

let ProfileStatusWithHooks:React.FC<PropsType> = (props) => {
    let [editMode,setEditMode] = useState(false);
    let [status,setStatus] = useState(props.status);

    const activateEditMode = () => {
        setEditMode(true)
    }

    const deactivateEditMode = () => {
        setEditMode(false)
        props.updateStatus(status);
    }

    const onStatusChange = (e) => {
        setStatus(e.currentTarget.value)
    }
    
    useEffect(() => {
            setStatus(props.status)
      },[props.status]);

        return(
            <div>
                {!editMode
                ?<span onDoubleClick={activateEditMode}>{!props.status?'Статуса нет':<div><b>Status: </b>{props.status}</div>}</span>
                :<input onChange={onStatusChange} onBlur={deactivateEditMode} autoFocus={true} value={status} ></input>}
            </div>
        )
    }
export default ProfileStatusWithHooks;
