import so from './Post.module.css';


function remove() {
    let removeElemet = window.document.getElementByClassName('so.item')
    document.body.removeChild(this.parentNode);
}
const Post = (props) => {
    return (
        <div className={so.item}>
            {props.message}
            <button>Like</button>Кол-во лайков: {props.likecount}
            <button onClick={remove}>Remove</button>
        </div>
    )
}

export default Post
