import profileReducer from './profile-reducer';
import dialogsReducer from './dialogs-reducer';
import sidebarReducer from './sidebar-reducer';

let store = {
_state: {
  profilePage: {
    posts: [
      {'id':'1', 'message':'Hi,how are you?', 'likesCount': 22},
      {'id':'2', 'message':'It\'s my first post', 'likesCount': 15}
    ],
  newPostText:'hhh'
  },
  dialogsPage: {
    messages: [
      {'id':1, 'message': 'Hi'},
      {'id':2, 'message': 'How are you?'},
      {'id':3, 'message': 'Yo'},
      {'id':4, 'message': 'Yo'}
    ],
    newMessageText:'',
    dialogs: [
      {'id':1, 'name': 'Dima'},
      {'id':2, 'name': 'Roof'},
      {'id':3, 'name': 'Andry'},
      {'id':4, 'name': 'Roma'}
    ]
  },
  sidebarPage:{
    sidebar: [
    {'id':1, 'name': 'Sveta', 'src':'https://i.playground.ru/p/_I__FEOa_NDhtQE5VzEEtQ.jpeg'},
    {'id':2, 'name': 'Roma', 'src':'https://i.playground.ru/p/_I__FEOa_NDhtQE5VzEEtQ.jpeg'},
    {'id':3, 'name': 'Dima', 'src':'https://i.playground.ru/p/_I__FEOa_NDhtQE5VzEEtQ.jpeg'}
  ],
  }
},
_callSubscriber() {
  console.log('ddd');
},
getState() {
  return this._state;
},
subscribe(observer) {
  this._callSubscriber = observer
},
dispatch(action) {
  this._state.profilePage = profileReducer(this._state.profilePage,action)
  this._state.dialogsPage = dialogsReducer(this._state.dialogsPage,action)
  this._state.sidebarPage = sidebarReducer(this._state.sidebarPage,action)


  this._callSubscriber(this._state)
  
}
}





window.state = store;
export default store;
