import {authAPI,securityAPI} from './../components/api/api';
import {stopSubmit} from 'redux-form'

const SET_USER_DATA = 'react/auth/SET_USER_DATA'
const SET_CAPTCHA_URL = 'react/auth/SET_CAPTCHA_URL'

let initialState =  {
    userId:null,
    email:null,
    login:null,
    isAuth:false,
    captchaUrl:null,
}

export const authReducer = (state = initialState,action) =>{
  switch(action.type) {
    case SET_USER_DATA: 
        return { 
            ...state,
            ...action.data
        }
    case SET_CAPTCHA_URL: 
    return { 
        ...state,
        ...action.payload
    }
    default: 
        return state;
  }
}
export const setAuthUserData = (userId,email,login,isAuth,captcha) => ({
  type:SET_USER_DATA, data:{userId,email,login,isAuth,captcha}
})
export const setCaptchaUrl= (captchaUrl) => ({
  type:SET_CAPTCHA_URL, payload:{captchaUrl}
})
export const getData = () => async (dispatch) => {
   let response = await authAPI.getData();
    if(response.resultCode === 0) {
      let {id,login,email} = response.data;
      
      dispatch(setAuthUserData(id, email, login,true));
  }
}
export const login = (email,password,rememberMe,captcha) => async (dispatch) => {
  let response = await authAPI.login(email,password,rememberMe,captcha);
  if(response.data.resultCode === 0) {
    dispatch(getData())
  } else {
    if (response.data.resultCode === 10) {
      dispatch(getCaptchaUrl())
    }
    let message = response.data.messages.length > 0 ?response.data.messages[0]:'Some error'
    dispatch(stopSubmit('login',{_error:message}))    
  }
}
export const getCaptchaUrl = () => async (dispatch) => {
  let response = await securityAPI.getCaptchaUrl();
  const captchaUrl = response.data.url
  dispatch(setCaptchaUrl(captchaUrl))
}
export const loginout = () => async (dispatch) => {
    let response = await authAPI.loginout()
    if(response.data.resultCode === 0) {
      dispatch(setAuthUserData(null, null, null,false));
  }
}
export default authReducer;
