import './App.css';
import React from 'react'
import HeaderContainer from './components/header/HeaderContainer';
import Navbar from './components/navbar/Navbar';
import UsersContainer from './components/users/UsersContainer';
import Login from './components/login/Login';
import {BrowserRouter,HashRouter,Route, Routes} from 'react-router-dom';
import {connect} from 'react-redux'
import {initializeApp} from './redux/app-reducer'
import {compose} from 'redux'
import Preloader from './components/common/preloader/Preloader';
import store from './redux/redux-store'
import {Provider} from 'react-redux';
import {Suspense} from 'react'

const DialogsContainer = React.lazy(() => import('./components/dialogs/DialogsContainer'))
const ProfileContainer = React.lazy(() => import('./components/profile/ProfileContainer'))

class App extends React.Component {

  componentDidMount() {
    this.props.initializeApp()
  }

  render() {
    if(!this.props.initialized) {
      return <Preloader />
    }
    return (
      <div >
        <header>
          <HeaderContainer />
        </header>
        <main>
          <Navbar /*sidebar={props.state.sidebarPage.sidebar}*//>
          <Suspense fallback={<div><Preloader /></div>}>
          <Routes>
                <Route path='/profile/:userId?' element={<ProfileContainer h='Profile'/>}/>
                <Route path='/dialogs/*' element={<DialogsContainer h='Dialogs'/>}/>
                <Route path='/users' element={<UsersContainer h='Users'/>}/>
                <Route path='/login' element={<Login h='Login'/>}/>
          </Routes>
          </Suspense>
        </main>
      </div>
    );
}
}

const mapStateToProps = (state) => ({
  initialized:state.app.initialized
})
let AppContainer = compose (
  connect(mapStateToProps,{initializeApp}),
)(App);

let AppMain = (props) => { 
  return <HashRouter>
  <Provider store={store}>
    <AppContainer />
  </Provider>
</HashRouter>
}

export default AppMain
