import axios  from "axios"

const instanse = axios.create({
    withCredentials:true,
    headers:{'API-KEY':'cafd4f76-51d0-40a7-93fa-907a2586d1e6'},
    baseURL:'https://social-network.samuraijs.com/api/1.0/'
})

export const userAPI = {
    getUsers(currentPage,pageSize) {
        return instanse.get(`users?page=${currentPage}&count=${pageSize}`).then(response => response.data)
    },
    follow(userId) {
        return instanse.post(`follow/${userId}`)
    },
    unfollow(userId) {
        return instanse.delete(`follow/${userId}`)
    },
    getProfile(profileId) {
        return profileAPI.getProfile(profileId);
    }

}
export const profileAPI = {
    getProfile(profileId) {
        return instanse.get(`profile/${profileId}`);
    },
    getStatus(profileId) {
        return instanse.get(`profile/status/${profileId}`);
    },
    updateStatus(status) {
        return instanse.put(`profile/status`,{status:status});
    },
}
export const authAPI = {
    getData() {
        return instanse.get(`auth/me`).then(response => response.data);
    },
    login(email,password,rememberMe = false) {
        return instanse.post(`auth/login`,{email,password,rememberMe});
    },
    loginout() {
        return instanse.delete(`auth/login`);
    },
}
