const FOLLOW = 'FOLLOW'
const UNFOLLOW = 'UNFOLLOW'
const SET_USERS = 'SET-USERS '


let initialState = {
  posts: [
      //{id:'1',followed:false,fullNmae:'Dmitry K.', status:'I am a Boss)', location:{city:'Minsk',country:'Belarus'}},
      //{id:'2',followed:true,fullNmae:'Oleg R.',status:'Live and learn',location:{city:'Moscow',country:'Russia' }},
      //{id:'3',followed:false,fullNmae:'Roma N.',status:'Scary',location:{city:'Pavlodar',country:'Kazakhstan' }}
    ]
}

const usersReducer = (state = initialState,action) =>{
    switch(action.type){
        case FOLLOW: {
          return { 
            ...state, 
            users:state.users.map( u => {
              if(u.id === action.userId) {
                return {...u, followed:true}
              }
              return u
            })
          };
        }
        case UNFOLLOW: {
          return { 
            ...state, 
            users:state.users.map( u => {
              if(u.id === action.userId) {
                return {...u, followed:false}
              }
              return u
            })
          };
        }
        case SET_USERS : {
          return {
            ...state, users:[...state.users,...action.users] 
          }
        }
        default: {
            return state;
        }
  }
}
export const followAC = (userId) => ({type:FOLLOW , userId})
export const unfollowAC = (userId) => ({type:UNFOLLOW,userId})
export const setUsersAC = (users) => ({type:SET_USERS,users})

export default usersReducer;
