import React, { useState }  from 'react'
import {useDispatch,useSelector} from 'react-redux'
import styles from './FormCreateTable.module.css'
import { actions } from '../../../redux/createTable-reducer.ts'
import { selectItems } from '../../../redux/createTable-selectors.ts'

const FormCreateTable = (props) => {
    const [valueOfItem,setValueOfItem] = useState('')
    const [valueOfDays,setValueOfDays] = useState('')
    const dispatch = useDispatch()
    const items = useSelector(selectItems)
    const addNewType = () => {
        dispatch(actions.setNewItem(valueOfItem))
        setValueOfItem('')
    }
    return (
        <div className={styles.formCreateTable}>
            <form className={styles.form}>

                <fieldset className={styles.fildset}>
                    <legend className={styles.legend}>Types</legend> 
                    <input type="text" required placeholder='Type' value={valueOfItem} onChange={(e) => setValueOfItem(e.currentTarget.value)} className={styles.inputs}/>
                    <div className={styles.add} onClick={() => addNewType()}>ADD</div>
                </fieldset>

                <fieldset className={styles.fildset}>
                    <legend className={styles.legend}>Days</legend> 
                    <input type="number" min="1" max="30" placeholder='Days' value={valueOfDays} onChange={(e) => {
                        if(parseInt(e.currentTarget.value) > 0 && parseInt(e.currentTarget.value) < 31) {
                            setValueOfDays(e.currentTarget.value)
                        }
                            
                    }} className={styles.inputs}/>
                </fieldset>

                <input type="submit" value="Create" className={styles.inputs + ' ' + styles.submit}/>

            </form>
            <h2 className={styles.h2}>Finish data</h2>
            <section className={styles.section}>
                <div>
                    <b className={styles.b}>Your`s types:</b>
                    {items && <ol>
                        {items.map(item => <li>{item}</li>)}
                    </ol>}
                </div>
                <div>
                    <b className={styles.b}>Days: {}</b>
                </div>
            </section>
        </div>
    )
}

export default FormCreateTable
