import {userAPI} from './../components/api/api';
import {updateObjectInArray} from './../utials/object-helpers'
const FOLLOW = 'FOLLOW'
const UNFOLLOW = 'UNFOLLOW'
const SET_USERS = 'SET_USERS'
const SET_CURRENT_PAGE = ' SET_CURRENT_PAGE'
const SET_TOTAL_USERS_COUNT = 'SET_TOTAL_USERS_COUNT'
const TOGGLE_IS_FETCHING = 'TOGGLE_IS_FETCHING'
const TOGGLE_IS_FOLLOWING_PROGRESS = 'TOGGLE_IS_FOLLOWING_PROGRESS'


let initialState = {
  users: [],
  pageSize: 5,
  totalUsersCount: 0,
  currentPage:1,
  isFetching:true,
  folowingInProgress:[],
}
//users: updateObjectInArray(state.users,action.userId,'id',{followed:true})
const usersReducer = (state = initialState,action) =>{
    switch(action.type){
      
        case FOLLOW: {
          return { 
            ...state, 
            users:items.map( u => {
              if(u.id === itemId) {
                return {...u, ...newObjProps}
              }
              return u
            })
          };
        }
        case UNFOLLOW: {
          return { 
            ...state, 
            users:updateObjectInArray(state.users,action.userId,'id',{followed:false})
          };
        }
        case SET_USERS : {
          return {
            ...state, users:action.users 
          }
        }
        case SET_CURRENT_PAGE : {
          return {
            ...state, currentPage:action.currentPage 
          }
        }
        case SET_TOTAL_USERS_COUNT : {
          return {
            ...state, totalUsersCount:11 
          }
        }
        case TOGGLE_IS_FETCHING : {
          return {
            ...state, isFetching:action.isFetching
          }
        }
        case TOGGLE_IS_FOLLOWING_PROGRESS : {
          return {
            ...state, folowingInProgress:action.isFetching
            ?[...state.folowingInProgress ,action.userId]
            :[state.folowingInProgress.filter(id => id !== action.userId)]
          }
        }
        default: {
            return state;
        }
  }
}
export const acceptFollow = (userId) => ({type:FOLLOW , userId})
export const acceptUnfollow = (userId) => ({type:UNFOLLOW,userId})
export const setUsersAC = (users) => ({type:SET_USERS,users})
export const setCurrentPage = (currentPage) => ({type:SET_CURRENT_PAGE, currentPage})
export const setTotalUsersCount = (totalUsersCount) => ({type:SET_TOTAL_USERS_COUNT, totalUsersCount})
export const toggleIsFetching = (isFetching) => ({type:TOGGLE_IS_FETCHING, isFetching})
export const toggleFollowingProgress = (isFetching,userId) => ({type:TOGGLE_IS_FOLLOWING_PROGRESS, isFetching,userId})


export const getUsers = (currentPage,pageSize) => { 
  return async (dispatch) => {
    dispatch(toggleIsFetching(true))
    dispatch(setCurrentPage(currentPage))
    let data = await userAPI.getUsers(currentPage,pageSize)
        dispatch(toggleIsFetching(false))
        dispatch(setUsersAC(data.items));
        dispatch(setTotalUsersCount(data.totalCount));
  }
}

const followUnfollowFlow = async (dispatch,userId,apiMethod,AC) => {
  dispatch(toggleFollowingProgress(true,userId));
  let response = await apiMethod(userId)
    if(response.data.resultCode === 0) {
      dispatch(AC(userId));
    }
  dispatch(toggleFollowingProgress(false,userId));
}

export const follow = (userId) => { 
  return async (dispatch) =>  {
    followUnfollowFlow(dispatch,userId,userAPI.follow.bind(userAPI),acceptFollow);
  }
}
export const unfollow = (userId) => { 
  return async (dispatch) =>  {
    followUnfollowFlow(dispatch,userId,userAPI.unfollow.bind(userAPI),acceptUnfollow);
  }
}
export default usersReducer;
