import {GetItemsType, instanse,ResponseType} from './api.ts'
import {PhotosType} from "../../types/types"
import { AxiosPromise } from 'axios'


export const userAPI = {
    getUsers(currentPage:number,pageSize:number,term:string,friend:null | boolean) {
        return instanse.get<GetItemsType>(`users?page=${currentPage}&count=${pageSize}&term=${term}`).then(response => response.data)
    },
    follow(userId:number) {
        return instanse.post<ResponseType>(`follow/${userId}`).then(res => res.data)
    },
    unfollow(userId:number) {
        return instanse.delete<ResponseType>(`follow/${userId}`).then(res => res.data) as Promise<ResponseType>
    }
}
