import { StatusType } from "../../redux/chat-reducer"

export type ChatMessageType = {
    message:string
    photo:string
    userId:number
    userName:string
}


type EventsNamesType = 'messages-received' | 'status-changed'
type MessagesReceivedSubscriberType = (messages:ChatMessageType[]) => void
type StatusChangedSubscriberType = (status:StatusType) => void
const subcribers = {
    'messages-received':[] as Array<MessagesReceivedSubscriberType>,
    'status-changed':[] as Array<StatusChangedSubscriberType>
}
let websocket:WebSocket
const closeHanbler = () => {
    notifySubscribersAboutStatus('pending')
    setTimeout(createChannel,3000)
}
const messageHandler = (e:MessageEvent) => {
    let newMessages = JSON.parse(e.data)
    subcribers['messages-received'].forEach(s => s(newMessages))
}
const openHandler = () => {
    notifySubscribersAboutStatus('ready')
}
const errorHandler = () => {
    notifySubscribersAboutStatus('error')
}
const notifySubscribersAboutStatus = (status:StatusType) => {
    subcribers["status-changed"].forEach(s => s(status))
}
const cleanUp = () => {
    websocket?.removeEventListener('close',closeHanbler)
    websocket?.removeEventListener('message',messageHandler)
    websocket?.removeEventListener('close',closeHanbler)
    websocket?.removeEventListener('message',messageHandler)
}
function createChannel() {
    cleanUp()
    websocket?.close()
    websocket = new WebSocket('wss://social-network.samuraijs.com/handlers/ChatHandler.ashx')
    subcribers["status-changed"].forEach(s => s('pending'))
    notifySubscribersAboutStatus('pending')
    websocket.addEventListener('close',closeHanbler) 
    websocket.addEventListener('message',messageHandler) 
    websocket.addEventListener('open',openHandler) 
    websocket.addEventListener('error',errorHandler) 
}  


export const chatAPI = {
    start() {
        createChannel()
    },
    stop() {
        subcribers['messages-received'] = []
        subcribers['status-changed'] = []
        websocket?.close()
        cleanUp() 
    },
    subscribe(eventName: EventsNamesType,callback:MessagesReceivedSubscriberType | StatusChangedSubscriberType) {
        subcribers[eventName].push(callback)
        return () => {
            subcribers[eventName] = subcribers[eventName].filter(s => s !== callback)
        }
    },
    unsubscribe(eventName: EventsNamesType,callback:MessagesReceivedSubscriberType | StatusChangedSubscriberType) {
        subcribers[eventName] = subcribers[eventName].filter(s => s !== callback)
    },
    sendMessage(message:string) {
        websocket.send(message)
    }
}
