import React, { useEffect } from 'react'
import styles from './List.module.css'
import { useDispatch, useSelector } from 'react-redux';
import { selectPurposes } from '../../../redux/plan-selectors.ts';
import { purposeType } from '../types/types';
import {actions} from '../../../redux/plan-reducer.ts';

const ListOfTodos = () => {
    const list = useSelector(selectPurposes)
    const dispatch = useDispatch()
    const deletePurpose = (purpose:purposeType) => {
        dispatch(actions.deletePurpose(purpose))
    }
    return (
        <>
        {list.length > 0 && <ul className={styles.listOfWork}>
            {list.map((item) => <div className={styles.item} key={item.id}>
                <b className={styles.text}>Your purpose: {item.text}</b>
                <p className={styles.text}>Date: {item.data}</p>
                <button className={styles.button} onClick={() => deletePurpose(item)}>+</button>
            </div>)}
        </ul>}
        </>
    )
}

export default ListOfTodos
