import Profile from './Profile.tsx';
import React from 'react';
import {connect}  from 'react-redux';
import {getProfile,getStatus,updateStatus,savePhoto,saveProfile} from '../../redux/profile-reducer.ts'
import {withAuthRedirect} from '../../hoc/withAuthRedirect.tsx'
import {
    useLocation,
    useNavigate,
    useParams,
  } from "react-router-dom";
import {compose} from 'redux'
import { AppStateType } from '../../redux/redux-store.js';

  function withRouter(Component) {
    function ComponentWithRouterProp(props) {
        let location = useLocation();
        let navigate = useNavigate();
        let params = useParams();
        return (
            <Component
                {...props}
                router={{ location, navigate, params }}
            />
        );
    }

    return ComponentWithRouterProp;
}

type MapPropsType = ReturnType<typeof mapStateToProps>
type MapDisplatchType = {
    getProfile,getStatus,updateStatus,savePhoto,saveProfile
}

class ProfileContainer extends React.Component<MapPropsType> {


refreshProfile() {
    let profileId = this.props.router.params.userId;
    if(!profileId) {
        profileId = this.props.userId;
    }
    this.props.getProfile(profileId);
    this.props.getStatus(profileId);   
}
componentDidMount() {
    this.refreshProfile()
}
componentDidUpdate(prevProps,prevState) {
    if(this.props.router.params.userId != prevProps.router.params.userId) {
        this.refreshProfile()
    }
}

    render() {
        return (
            <Profile 
                {...this.props} 
                isOwner={!this.props.router.params.userId} 
                profile={this.props.profile} 
                status={this.props.status} 
                updateStatus={this.props.updateStatus}
                savePhoto={this.props.savePhoto}
                saveProfile={this.props.saveProfile}
            />
        )
    }
}

let mapStateToProps = (state:AppStateType) => ({
    profile:state.profilePage.profile,
    status:state.profilePage.status,
    userId:state.auth.userId,
    isAuth:state.auth.isAuth,
    error:state.profilePage.error
})

export default compose(
    connect(mapStateToProps,{getProfile,getStatus,updateStatus,savePhoto,saveProfile}),
    withRouter,
    withAuthRedirect
)(ProfileContainer)
