import React, { useEffect}  from 'react'
import styles from './Table.module.css'
import { useDispatch, useSelector } from 'react-redux'
import { useNavigate, useParams } from 'react-router-dom'
import { selectTable } from '../../../../redux/table-selectors'
import { deleteTableById, tableFullType} from '../../../../redux/tables-reducer'
import {v1} from 'uuid'
import { actions, getTable } from './../../../../redux/table-reducer'

const Table:React.FC = () => {
    const table:tableFullType = useSelector(selectTable)
    const navigate = useNavigate()
    const dispatch = useDispatch()
    const {tableId} = useParams()
    
    useEffect(() => {
        dispatch(getTable(tableId))
    },[])

    const changeValue = (row:number,column:number,newValue:string) => {
        let updatedData = table.tableData.data.map((rowData:any, index:number) => {
            if (index === row) {
                let updatedStatus = [...rowData.status]; 
                if (newValue === "-") {
                    updatedStatus[column] = "+";
                } else if (newValue === "+") {
                    updatedStatus[column] = "-";
                }
                return {
                    ...rowData,
                    status: updatedStatus,
                };
            }
            return rowData;
        });
    
        const updatedTableData = {
            ...table.tableData,
            data: updatedData,
        };
    
        const updatedTable = {
            ...table,
            tableData: updatedTableData,
        };
        dispatch(actions.updateTable(updatedTable));
    }


    const deleteTable = (id:string) => {
        de
        dispatch(deleteTableById(id))
    }


    if (table === null) {
        navigate('/tables')
        return
    } else if(Object.keys(table).length === 0) {
        return 
    }
    return (
        <>
        <div className={styles.container}>
                <h2>{table.title}<span className={styles.bucket} onClick={() => deleteTable(table._id)}>ðŸ—‘</span></h2>
            <table className={styles.table}>
                <thead>
                    <tr>
                        {table.tableData.days.map(day => <th key={day} className={styles.th}>{day}</th>)}
                    </tr>
                </thead>
                <tbody>
                    {table.tableData.data.map((rowData,indexRow) => {
                        return (
                            <tr key={v1()}> 
                                <td className={styles.td}>{rowData.name}</td>
                                {rowData.status.map((value,indexValue) => {
                                    return <td key={v1()} className={styles.td} onClick={(event) => changeValue(indexRow,indexValue,event.currentTarget.innerHTML)}>{value}</td>
                                })}
                            </tr>
                        )
                    })}
                </tbody>
            </table>
        </div>
        </>
        
    )
}

export default Table
