import React, { useEffect }  from 'react'
import styles from './Table.module.css'
import { useDispatch, useSelector } from 'react-redux'
import { Navigate, useParams } from 'react-router-dom'
import { selectTables } from '../../../../redux/tables-selectors.ts'
import { tableType } from '../../../../redux/tables-reducer'

const Table = () => {
    const dispatch = useDispatch()
    const {tableId} = useParams()
    const tables = useSelector(selectTables)
    const tableData:tableType | undefined = tables.find(table => table.id === tableId)
    tableData?.tableData.days.unshift('ㅤ')
    const changeStatus = (e) => {
        if(e.currentTarget.innerHTML = 'ㅤ') {
            e.currentTarget.innerHTML = '✔'
        } else{
            e.currentTarget.innerHTML = 'ㅤ'
        }
    }
    return (
        <>
        {!tableData ? <Navigate to="/tables" /> :
        <div className={styles.container}>
            <h2>{tableData.name}</h2>
            <table className={styles.table}>
                <thead>
                    <tr>
                        {tableData.tableData.days.map(day => <th key={day} className={styles.th}>{day}</th>)}
                    </tr>
                </thead>
                <tbody>
                    {tableData.tableData.items.map(item => <tr key={item}>
                        <td className={styles.item + ' '+ styles.td}>{item}</td>
                        {tableData.tableData.days.map(cell => {
                            if(cell === 'ㅤ') return
                            return <td className={styles.td} key={cell} onClick={(e) => changeStatus(e)}>ㅤ</td>})}
                    </tr>)}
                </tbody>
            </table>
        </div>}
        </>
        
    )
}

export default Table
