import { ThunkAction } from 'redux-thunk';
import {getData} from './auth-reducer.ts'
import { AppStateType } from './redux-store';

const SET_INITIALIZED = 'SET_INITIALIZED'

export type InitialStateType = {
  initialized:boolean
}

let initialState:InitialStateType = {
  initialized:false,
}

export const appReducer = (state:InitialStateType = initialState,action:InitializedSuccesActionType):InitialStateType =>{
  switch(action.type) {
    case SET_INITIALIZED: 
        return { 
            ...state,
            initialized:true
        }
    default: 
        return state;
  }
}

type InitializedSuccesActionType = {
  type: typeof SET_INITIALIZED
}
export const initializedSucces = ():InitializedSuccesActionType => ({type:SET_INITIALIZED});


type ThunkType = ThunkAction<Promise<void>,AppStateType,unknown,InitializedSuccesActionType>
export const initializeApp = () => {
  return (dispatch:any) =>  {
    let promise = dispatch(getData());
    promise.then(() => dispatch(initializedSucces()))
   
  }
}


export default appReducer;
