import { ThunkAction } from 'redux-thunk';
import {getData} from './auth-reducer.ts'
import { AppStateType } from './redux-store';

const SET_INITIALIZED = 'SN/APP/SET_INITIALIZED'

export type InitialStateType = typeof initialState

let initialState = {
  initialized:false,
}

export const appReducer = (state:InitialStateType = initialState,action:ActionsType):InitialStateType =>{
  switch(action.type) {
    case SET_INITIALIZED: 
        return { 
            ...state,
            initialized:true
        }
    default: 
        return state;
  }
}

type InitializedSuccesActionType = {
  type: typeof SET_INITIALIZED
}

const actions = {
  initializedSucces:():InitializedSuccesActionType => ({type:SET_INITIALIZED})
}

type ThunkType = ThunkAction<void,AppStateType,unknown,ActionsType>

export const initializeApp = ():ThunkType => {
  return (dispatch) =>  {
    let promise = dispatch(getData());
    promise.then(() => dispatch(actions.initializedSucces()))
  }
}


export default appReducer;
