const ADD_MESSAGE = 'ADD-MESSAGE'
const UPDATE_NEW_MESSAGE_TEXT = 'UPDATE-NEW-MESSAGE-TEXT'


let initialState = {
  messages: [
    {'id':1, 'message': 'Hi'},
    {'id':2, 'message': 'How are you?'},
    {'id':3, 'message': 'Yo'},
    {'id':4, 'message': 'Yo'}
  ],
  newMessageText:'',
  dialogs: [
    {'id':1, 'name': 'Dima'},
    {'id':2, 'name': 'Roof'},
    {'id':3, 'name': 'Andry'},
    {'id':4, 'name': 'Roma'}
  ]
},
sidebarPage:{
  sidebar: [
  {'id':1, 'name': 'Sveta', 'src':'https://i.playground.ru/p/_I__FEOa_NDhtQE5VzEEtQ.jpeg'},
  {'id':2, 'name': 'Roma', 'src':'https://i.playground.ru/p/_I__FEOa_NDhtQE5VzEEtQ.jpeg'},
  {'id':3, 'name': 'Dima', 'src':'https://i.playground.ru/p/_I__FEOa_NDhtQE5VzEEtQ.jpeg'}
],
}

export const dialogsReducer = (state,action) =>{
  switch(action.type){
    case ADD_MESSAGE:
      let newMessage = {
        'id':5,
        'message':state.newMessageText
      }
      state.messages.push(newMessage);
      state.newMessageText = '';
      return state;
    case UPDATE_NEW_MESSAGE_TEXT:
      state.newMessageText = action.newMessage;
      return state;
    default:
      return state;
  }
}
export const addMessageActionCreater = () => ({
  type:ADD_MESSAGE
})
export const updateNewMessageTextActionCreater = (message) => 
({ type:UPDATE_NEW_MESSAGE_TEXT, newMessage:message})

export default dialogsReducer;
