import { Formik ,Form , Field} from 'formik';
import { isSubmitting } from 'redux-form';
import React from 'react';
import { SearchFilterType } from '../../redux/users-reducer';


const UsersSearchFormValidate = (values:any) => {
    const errors = {}
    return errors
}
type PropsType = {
    onFilterChanged:(filter:SearchFilterType) => void
}
const UsersSearchForm:React.FC<PropsType> = React.memo((props) => {
    const submit = (values:SearchFilterType,{setSubmitting}:{setSubmitting :(isSubmitting:boolean) => void}) => {
        props.onFilterChanged(values)
    }
    return <div>
            <Formik 
                onSubmit={submit} 
                validate={UsersSearchFormValidate}
                initialValues={{term:""}}
            >
            {({isSubmitting}) => (
                <Form>
                    <Field type="text" name="term" />
                    <Field as="select" name="friend">
                        <option value="onlyfriend">All</option>
                        <option value="onlyfriend">Friends</option>
                    </Field>
                    <button type="submit" disabled={isSubmitting}>Find</button>
                </Form> 
            )}
            </Formik>
        </div>
})

export default UsersSearchForm
