import { Navigate } from "react-router-dom";
import React from 'react';
import {connect}  from 'react-redux';
import { AppStateType } from "../redux/redux-store";

let mapStateToPropsForRedirect = (state:AppStateType) => ({
    isAuth:state.auth.isAuth
});
type MapPropsType = {
    isAuth:boolean
}

export function withAuthRedirect<WCP extends JSX.IntrinsicAttributes>(WrappedComponent:React.ComponentType<WCP> ) {
    const RedirectComponent:React.FC<MapPropsType> = (props) => {
        let {isAuth,...resProps} = props
        if(!isAuth) return <Navigate to='/login' />
        return <WrappedComponent {...resProps as WCP} />
    }
    let ConnectedAuthRedirectComponent = connect<MapPropsType,DispatchPropsTypeWCP,AppStateType>(mapStateToPropsForRedirect)(RedirectComponent);
    return ConnectedAuthRedirectComponent;
}
