import './App.css';
import React from 'react'
import HeaderContainer from './components/header/HeaderContainer.tsx';
import Navbar from './components/navbar/Navbar.tsx';
import UsersPage from './components/users/UsersContainer.tsx';
import Login from './components/login/Login.tsx';
import {BrowserRouter,Route, Routes,Navigate} from 'react-router-dom';
import {connect} from 'react-redux'
import {initializeApp} from './redux/app-reducer.ts'
import {compose} from 'redux'
import Preloader from './components/common/preloader/Preloader.tsx'
import store, { AppStateType } from './redux/redux-store.ts'
import {Provider} from 'react-redux';
import {Suspense} from 'react'
import 'antd/dist/reset.css';

const DialogsContainer = React.lazy(() => import('./components/dialogs/DialogsContainer.tsx'))
const ProfileContainer = React.lazy(() => import('./components/profile/ProfileContainer.tsx'))


type DispatchPropsType = {
  initializeApp:() => void
}
type MapPropsType = ReturnType<typeof mapStateToProps>

class App extends React.Component<DispatchPropsType & MapPropsType> {
  catchAllUnhandleErrors = (e: PromiseRejectionEvent) => {
    alert('Problem')
  }
  componentDidMount() {
    this.props.initializeApp();
    window.addEventListener("unhandledrejection",this.catchAllUnhandleErrors)
  }
  componentWillUnmount() {
    window.removeEventListener("unhandledrejection",this.catchAllUnhandleErrors)
  }
  render() {
    if(!this.props.initialized) {
      return <Preloader />
    }
    return (
      <div >
        <header>
          <HeaderContainer />
        </header>
        <main>
          <Navbar /*sidebar={props.state.sidebarPage.sidebar}*//>
          <Suspense fallback={<div><Preloader /></div>}>
          <Routes>
                <Route path='/' element={<Navigate to="/profile" />}/>
                <Route path='/profile/:userId?' element={<ProfileContainer h='Profile'/>}/>
                <Route path='/dialogs/*' element={<DialogsContainer h='Dialogs'/>}/>
                <Route path='/users' element={<UsersPage h='Users'/>}/>
                <Route path='/login' element={<Login h='Login'/>}/>
                <Route path='*' element={<div>404 </div>}/>
          </Routes>
          </Suspense>
        </main>
      </div>
    );
}
}

const mapStateToProps = (state:AppStateType) => ({
  initialized:state.app.initialized
})
let AppContainer = compose<React.ComponentType>(
  connect(mapStateToProps,{initializeApp}),
)(App);

let AppMain:React.FC = () => { 
  return <BrowserRouter>
  <Provider store={store}>
    <AppContainer />
  </Provider>
</BrowserRouter>
}

export default AppMain
